import React from "react"
import {render, fireEvent} from "@testing-library/react"
import { shallow } from 'enzyme';
import renderer from 'react-test-renderer';
import HelloWorld from './HelloWorld'

  it("Check if it renders", () => {
    const { queryByTitle } = render(<HelloWorld/>);
    const hello = queryByTitle('helloTitle');
      expect(hello).toBeTruthy();
  });

  describe('try to render a prop', () => {
    it('should render without throwing an error ', () => {
      const component = renderer.create(<HelloWorld/>);
      expect(
        shallow(<HelloWorld/>)
          .find('div')
          .exists()
      ).toBe(true);
    });
  });


  describe('try to render a prop as html', () => {
    it("sends a prop", () => {
      const { queryByTitle } = render(<HelloWorld/>);
      const hello = queryByTitle('helloTitle');
      const paragraph = screen.findByText('Hello !');

      expect(hello.paragraph).toBeInTheDocument();
    });
  });
