import React from 'react'
import City from './City'
import Button from '../Exo3/Button';

function Zone2({cityList, onDelete, onSearch}) {

  return (
    <>

    <Button value={'Search'} onSearch={onSearch} city={cityList}/>

    <div className="flex">

      {cityList.map((a, i) => {
        if (a.name.toLowerCase === "paris" || a.name.toLowerCase === "lyon" || a.name.toLowerCase === "marseille" || a.name.toLowerCase === "toulouse" || a.name.toLowerCase === "lille" || a.name.toLowerCase === "bordeau") {
          return (
            <>
            <City className="absolute" key={i} city={a} onDelete={onDelete}/>
            </>
          )
        }

      })}
    </div>
    </>
  )
}

export default Zone2
