import React, { useState } from 'react'

const split = (expression, operator) => {
  const result = [];
  let braces = 0;
  let currentChunk = "";
  for (let i = 0; i < expression.length; ++i) {
    const curCh = expression[i];
    if (curCh == '(') {
      braces++;
    } else if (curCh == ')') {
      braces--;
    }
    if (braces == 0 && operator == curCh) {
      result.push(currentChunk);
      currentChunk = "";
    } else currentChunk += curCh;
  }
  if (currentChunk != "") {
    result.push(currentChunk);
  }
  return result;
};

// this will only take strings containing * operator [ no + ]
const parseMultiplicationSeparatedExpression = (expression) => {
  const numbersString = split(expression, '*');
  console.log(numbersString)
  const numbers = numbersString.map(noStr => {
    if (noStr[0] == '(') {
      const expr = noStr.substr(1, noStr.length - 2);
      // recursive call to the main function
      return parsePlusSeparatedExpression(expr);
    }
    return +noStr;
  });
  const initialValue = 1.0;
  const result = numbers.reduce((acc, no) => acc * no, initialValue);
  return result;
};

// this will only take strings containing / operator [ no + ]
const parseDivisionSeparatedExpression = (expression) => {

  const numbersString = split(expression, '/');
  console.log(numbersString)
  const numbers = numbersString.map(noStr => {
    if (noStr[0] == '(') {
      const expr = noStr.substr(1, noStr.length - 2);
      // recursive call to the main function
      return parsePlusSeparatedExpression(expr);
    }
    return +noStr;
  });
  const initialValue = 1.0;
  const result = numbers.reduce((acc, no) => acc / no, initialValue);
  return result;
};

// * / -
const parseMinusSeparatedExpression = (expression) => {
  const [numbers, setNumbers] = useState([''])
  const numbersString = split(expression, '-');

  if(numbersString.includes('*')) {
    setNumbers(numbersString.map(noStr => parseMultiplicationSeparatedExpression(noStr)))
  }
  else if(numbersString.includes('/')){
    setNumbers(numbersString.map(noStr => parseDivisionSeparatedExpression(noStr)))
  }

  const initialValue = numbers[0];
  const result = numbers.slice(1).reduce((acc, no) => acc - no, initialValue);
  return result;
};

// * / - + 
const parsePlusSeparatedExpression = (expression) => {
  const numbersString = split(expression, '+');
  const numbers = numbersString.map(noStr => parseMinusSeparatedExpression(noStr));
  const initialValue = 0.0;
  const result = numbers.reduce((acc, no) => acc + no, initialValue);
  return result;
};

const Parse = ({expression}) => {

  const search = ',';
  const replaceWith = '.';
  const res = expression.split(search).join(replaceWith);
  const result = parsePlusSeparatedExpression(res, '+');

  return (
    <>
    <div className="flex m-4">
      <p >{expression} is equal to : {result}</p>
    </div>
    </>
  )
};

export default Parse;
