import React from 'react'
import { FaTimes } from 'react-icons/fa';

function Zone2({cityList}) {
  function handleDelete(item){
    item.destroy;
  }

  return (
    <div>
      {cityList.map(a => {
        if (a.name === "paris" || a.name === "lyon" || a.name === "marseille" || a.name === "touoluse" || a.name === "lille" || a.name === "bordeau") {
          return (
            <button className="flex bg-gray border border-black rounded px-2 mx-2 capitalize">{a.name + " "} <FaTimes onclick={handleDelete(a)} className="bg-gray border border-black rounded my-auto ml-1"/></button>
          )
        }
      })}
    </div>
  )
}

export default Zone2
