import React from "react"
import {render, fireEvent} from "@testing-library/react"
import { shallow } from 'enzyme';
import renderer from 'react-test-renderer';
import HelloWorld from './HelloWorld'
import ReactTestUtils from 'react-dom/test-utils'; // ES6


  it("Check if it renders", () => {
    const { queryByTitle } = render(<HelloWorld/>);
    const hello = queryByTitle('helloTitle');
      expect(hello).toBeTruthy();
  });

  describe('try to render a prop', () => {
    it('should render without throwing an error ', () => {
      const component = renderer.create(<HelloWorld/>);
      expect(
        shallow(<HelloWorld/>)
          .find('div')
          .exists()
      ).toBe(true);
    });
  });

  describe('updating the value', function(){
    var component;
    beforeEach(function(){
        component = ReactTestUtils.renderIntoDocument(<HelloWorld greeting = {'Hello !'} />);
    });

    it('should update the state of the component when the value prop is changed', function(){
        // Act
        component.props.value = ('Hello !');
        component.forceUpdate();
        // Assert
        expect(component.state.value).toBe('Hello !');
    });
});
