import React from 'react'
import { FaTimes } from 'react-icons/fa';

function Zone2({cityList, onDelete}) {

  return (
    <div className="flex">
      {cityList.map(a => {
        if (a.name === "paris" || a.name === "lyon" || a.name === "marseille" || a.name === "touoluse" || a.name === "lille" || a.name === "bordeau") {
          return (
            <button key={a.name} className="flex bg-gray border border-black rounded px-2 mx-2 capitalize">{a.name + " "} <button className="bg-gray border border-black rounded my-auto ml-1"> {a.id}<FaTimes onclick={() => onDelete(a.id)}/></button></button>
          )
        }
      })}
    </div>
  )
}

export default Zone2
