import React, { useEffect, useState } from 'react'

function Fetch( ) {

  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch(`/products`, {
      method: 'get',
    })    
    .then((res) => {
      return res.json()
    })
    .then((data) => {
      setProducts(data.products);
    })
    .catch(function () {
      console.log("error mutafuka");
    });

  }, []);

  console.log(products);

  return (
    <div className="">
      <h1 className="mb-6">Here are the listed items : </h1>
      {products.map((a, i) => {
        return (
          <table class="table-fixed">
            <thead>
              <tr>
                <th className="w-1/4"> Id   </th>
                <th> Ref  </th>
                <th> Name </th>
                <th> City </th>
                <th> Price</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{a.id}</td>
                <td>{a.prodRef}</td>
                <td>{a.prodName}</td>
                <td>{a.prodCity}</td>
                <td>{a.prodPrice}</td>
              </tr>

            </tbody>
          </table>
        )
        })}
    </div>
  )
}

export default Fetch
