import React, { useEffect, useState } from 'react'

function Fetch( ) {

  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch(`/home`, {
      method: 'get',
    })    
  .then((res) => console.log(res.products))
  .then((res) => setProducts(res))


  .catch(function () {
    console.log("error");
  });

  }, []);

  return (
    <div>
      <h1>hello fetch</h1>
      <p></p>
    </div>
  )
}

export default Fetch
