import React from "react"
import {render, fireEvent} from "@testing-library/react"
import { shallow } from 'enzyme';
import renderer from 'react-test-renderer';
import HelloWorld from './HelloWorld'

  it("Check if it renders", () => {
    const { queryByTitle } = render(<HelloWorld/>);
    const hello = queryByTitle('helloTitle');
      expect(hello).toBeTruthy();
  });

  describe('try to render a prop', () => {
    it('should render without throwing an error ', () => {
      const component = renderer.create(<HelloWorld/>);
      expect(
        shallow(<HelloWorld/>)
          .find('div')
          .exists()
      ).toBe(true);
    });
  });

  describe('updating the value', function(){
    var component;

    beforeEach(function(){
        component = TestUtils.renderIntoDocument(<HelloWorld value={true} />);
    });

    it("Component should call componentWillReceiveProps on update", () => {
      const spy = sinon.spy(Component.prototype, "componentWillReceiveProps");
      const wrapper = shallow(<Component {...props} />);

      expect(spy.calledOnce).to.equal(false);
      wrapper.setProps({ prop: 2 });
      expect(spy.calledOnce).to.equal(true);
  });
});
