import React from 'react'
import { FaTimes } from 'react-icons/fa';

function Zone2({cityList}) {

  function handleDelete(item){
    cityList.filter(a => a.name === item.name)
    console.log('bite')
  }

  return (
    <div className="flex">
      {cityList.map(a => {
        if (a.name === "paris" || a.name === "lyon" || a.name === "marseille" || a.name === "touoluse" || a.name === "lille" || a.name === "bordeau") {
          return (
            <button key={a.name} className="flex bg-gray border border-black rounded px-2 mx-2 capitalize">{a.name + " "} <button onClick={handleDelete} className="bg-gray border border-black rounded my-auto ml-1"> <FaTimes /></button></button>
          )
        }
      })}
    </div>
  )
}

export default Zone2
